var sg = require('./index.js')(process.env.SENDGRID_APIKEY);

var email = new sg.Email();

email.to = 'eddiezane@sendgrid.com';
email.subject = 'Testing addBcc';
email.text = 'yo';
email.from = 'taco@cat.limo';
email.addBcc('eddiezane@gmail.com');
email.smtpapi.addCategory('test');

console.log(email.smtpapi.jsonString());

sg.send(email, function(err, json) {
  if (err) console.error(err);
  console.log(json);
});
